mapper.insert:
INSERT INTO t_board_cmt
SET iboard = #{iboard}
, iuser = #{iuser}
, ctnt = #{ctnt}

mapper.update:
UPDATE t_board_cmt
SET ctnt = #{ctnt}
, updated_at = current_timestamp()
WHERE iboard_cmt = #{iboardCmt}
AND iuser = #{iuser}

mapper.delete:
DELETE FROM t_board_cmt
WHERE

    iboard_cmt = #{iboardCmt}
    AND iuser = #{iuser}


    iboard = #{iboard}

mapper.select:
SELECT COUNT(iboard_cmt)
FROM t_board_cmt
WHERE iboard = #{iboard}
